from util import get_data, get_output_path, reverse_complement, rna_to_protein


def get_substrings_encoding_peptide(dna, peptide):
    rna_basic = dna.replace('T', 'U')
    substrings = []

    for rna in [rna_basic, reverse_complement(rna_basic)]:
        for shift in range(3):
            reading_frames = rna_to_protein(rna[shift:])
            # print(reading_frames)

            start = -1
            while peptide in reading_frames[start+1:]:
                start = reading_frames.index(peptide, start+1)
                start_rna = start*3+shift
                substring = rna[start_rna:start_rna + len(peptide) * 3]
                
                if reverse_complement(rna_basic) == rna:
                    substring = reverse_complement(substring)
                    
                substrings.append(substring.replace('U', 'T'))

    return substrings


if __name__ == "__main__":
    data = get_data(__file__)
#     data ='''
# KEVFEPHYY'''
#     data = '''ATGGCCATGGCCCCCAGAACTGAGATCAATAGTACCCGTATTAACGGGTGA
# MA'''

    dna, peptide = data.split('\n')
    
    substrings = get_substrings_encoding_peptide(dna, peptide)
    
    # for substring in substrings:
    #     print(rna_to_protein(substring.replace('T', 'U')), rna_to_protein(reverse_complement(substring.replace('T', 'U'))))

    with open(get_output_path(__file__), "w") as f:
        print(*substrings, sep='\n')
        print(*substrings, sep='\n', file=f)
        